import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import styled from 'styled-components';

const SidebarLink = styled(Link)`
    display: flex;
    color: #e1e9fc;
`

const SidebarSubM = ({ item }) => {
    const [subnav, setSubnav] = useState(false)

    const showSubnav = () => setSubnav(!subnav)
    return (
        <>
            <SidebarLink to={item.path} className="SidebarLink" onClick={item.subNav && showSubnav}>
                <div>
                    {item.icon}
                    <div className="SidebarLabel">{item.title}</div>
                </div>
                <div>
                    {item.subNav && subnav ? item.iconOpened : item.subNav ? item.iconClosed : null}
                </div>
            </SidebarLink>
            {subnav && item.subNav.map((item, index) => {
                return (
                    <div to={item.path} key={index} className="DropdownLink">
                        {item.icon}
                        <div className="SidebarLabel">{item.title}</div>
                    </div>
                )
            })}
        </>
    )
}

export default SidebarSubM
